import { useEffect, useState } from 'react';
import axios from 'axios';


const instance = axios.create({
    method: 'get',  
    responseType: 'json',
    baseURL: 'https://api.github.com',
    headers: {
        Authorization: "bearer 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"
    }
});

const fetchData = async (userData) => {
    const QUERY_ORGANIZATION = `query {
        organization(login: "facebook") {
            name
            email
            id
            url
            description
        }
    }`;
    const { data } = await instance.post(
        '/graphql',
        { query: QUERY_ORGANIZATION }
    );
    userData[1](data);
}

function GithubGraphQL1() {
    let userData = useState(null);

    useEffect(() => {
        fetchData(userData);
    });

    return (
        <div>
            <pre>
                UserData:
                {userData[0] && JSON.stringify(userData[0], null, 4)}
            </pre>
        </div>
    );
}
export default GithubGraphQL1;
