import { useEffect, useState } from 'react';
import axios from 'axios';


const instance = axios.create({
    method: 'get',
    responseType: 'json',
    baseURL: 'https://api.github.com',
    headers: {
        Authorization: "bearer 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"
    }
});

const FetchData = async () => {
    const QUERY_ORGANIZATION = `query {
        organization(login: "facebook") {
            name
            description
        }
    }`
    const data = await instance.post(
        '/graphql',
        { query: QUERY_ORGANIZATION }
    );
    console.log(data);
    return(data);
}

function GithubGraphQL1() {
    let [userData, setUserData] = useState(null);

    useEffect(() => {
        const apiData = FetchData();
        setUserData(apiData);
        console.log(apiData);
    }, []);

    return (
        <div>
            UserData:
            {userData && JSON.stringify(userData, null, 4)}
        </div>
    );
}
export default GithubGraphQL1;
